    function selectChanged(id,valor){
        let actual = id.split("-");
        if(valor == "0"){
            $("#"+actual[0]+"-value").text("0%");
        } else {
            $("#"+actual[0]+"-value").text("100%");
        }
        var total = 0;
        for(i = 1;i <=28;i++){
            let elemento = $("#"+i+"-value").text();
            let valor = elemento.split("%");
            total = total + parseInt(valor[0]);
        }
        total = total /28;
        $("#total").text(Math.round(total) + "%");
    }
